import { useRef } from 'react';
import './FeaturedCompanies.css';

const companies = [
  { 
    name: 'Atos', 
    logo: 'https://www.netapp.com/media/atos-1200x625_tcm19-33920.png?v=87951' 
  },
  { 
    name: 'TCS', 
    logo: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTM2dYSnXYEQ72fd6VCHQSYzGGaRNUoTAsyRw&s' 
  },
  { 
    name: 'Infosys', 
    logo: 'data:image/png;base64,' 
  },
  { 
    name: 'Capgemini', 
    logo: 'data:image/png;base64,' 
  },
  { 
    name: 'Cognizant', 
    logo: 'data:image/png;base64,' 
  },
  { 
    name: 'Zensar', 
    logo: 'data:image/png;base64,' 
  },
  { 
    name: 'ICICI', 
    logo: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSpl-6e_Socggp72x2L6oKolJm2cFTyN52X7A&s' 
  },
  { 
    name: 'Mindteck', 
    logo: 'data:image/png;base64,' 
  }
];

export default function FeaturedCompanies() {
  const trackRef = useRef(null);

  const scrollByCard = (direction) => {
    const track = trackRef.current;
    if (!track) return;
    const card = track.querySelector('.nsfc-card');
    const amount = card ? card.getBoundingClientRect().width + 24 : 280;
    track.scrollBy({ left: direction * amount, behavior: 'smooth' });
  };

  return (
    <section className="nsfc-section" aria-label="Featured Companies">
      <div className="nsfc-header">
        <h2>Featured Companies</h2>
        <div className="nsfc-controls" aria-hidden>
          <button className="nsfc-arrow" onClick={() => scrollByCard(-1)} aria-label="Previous">
            ‹
          </button>
          <button className="nsfc-arrow" onClick={() => scrollByCard(1)} aria-label="Next">
            ›
          </button>
        </div>
      </div>

      <div className="nsfc-viewport">
        <div className="nsfc-track" ref={trackRef}>
          {companies.map((c) => (
            <div key={c.name} className="nsfc-card">
              <div className="nsfc-logo-wrap">
                <img src={c.logo} alt={`${c.name} logo`} loading="lazy" />
              </div>
              <div className="nsfc-name">{c.name}</div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}


